// Pasar la cola completa al siguiente nodo para su procesamiento
return { payload: cola };

const RELLENANDO_KEY = "rellenando";
const FLUJOS_RECIENTES_KEY = "flujosRecientes";
const COLA_VACIADO_KEY = "colaVaciado";

function calcularFlujo(cola) {
    if (cola.length < 2) return 0;

    const primerRegistro = cola[0];
    const ultimoRegistro = cola[cola.length - 1];
    const tiempoDiferencia = (ultimoRegistro.tiempo - primerRegistro.tiempo) / 1000; // en segundos

    const volumenInicial = ((primerRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);
    const volumenFinal = ((ultimoRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);

    if (tiempoDiferencia > 0) {
        return Math.round(-((volumenFinal - volumenInicial) / tiempoDiferencia) * 3600);
    }
    return 0;
}

function estaRellenando(cola) {
    if (cola.length < 2) return false;
    const cambioVolumen = cola[cola.length - 1].volumen - cola[0].volumen;
    return cambioVolumen > 150;
}

function obtenerPromedioFlujo(flujosRecientes) {
    if (flujosRecientes.length === 0) return 0;
    const suma = flujosRecientes.reduce((a, b) => a + b, 0);
    return Math.round(suma / flujosRecientes.length);
}

// Función principal
const cola = msg.payload;
let flujo = 0;
let rellenando = flow.get(RELLENANDO_KEY) || false;
let flujosRecientes = flow.get(FLUJOS_RECIENTES_KEY) || [];
let colaVaciado = flow.get(COLA_VACIADO_KEY) || [];

if (estaRellenando(cola)) {
    if (!rellenando) {
        rellenando = true;
        flow.set(RELLENANDO_KEY, true);
        node.status({fill:"yellow", shape:"ring", text:"Rellenando - Usando flujo promedio"});
    }
    flujo = obtenerPromedioFlujo(flujosRecientes);
    colaVaciado = []; // Reiniciar la cola de vaciado
} else {
    if (rellenando) {
        // El rellenado acaba de terminar
        rellenando = false;
        flow.set(RELLENANDO_KEY, false);
        colaVaciado = []; // Iniciar una nueva cola de vaciado
        node.status({fill:"green", shape:"dot", text:"Operación normal - Iniciando nuevo vaciado"});
    }

    // Añadir el nuevo registro a la cola de vaciado
    colaVaciado.push(cola[cola.length - 1]);

    // Mantener solo los últimos 10 registros en la cola de vaciado
    if (colaVaciado.length > 10) colaVaciado.shift();

    // Calcular el flujo usando la cola de vaciado
    flujo = calcularFlujo(colaVaciado);

    // Actualizar flujos recientes solo si es una medición válida
    if (flujo > 0 && flujo <= 10000) {
        flujosRecientes.push(flujo);
        if (flujosRecientes.length > 10) flujosRecientes.shift();
        flow.set(FLUJOS_RECIENTES_KEY, flujosRecientes);
    }
}

// Guardar la cola de vaciado actualizada
flow.set(COLA_VACIADO_KEY, colaVaciado);

return { payload: flujo };

