function calcularFlujo(cola) {
    if (cola.length < 2) return 0;

    const primerRegistro = cola[0];
    const ultimoRegistro = cola[cola.length - 1];
    const tiempoDiferencia = (ultimoRegistro.tiempo - primerRegistro.tiempo) / 1000; // en segundos

    const volumenInicial = ((primerRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);
    const volumenFinal = ((ultimoRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);

    if (tiempoDiferencia > 0 && volumenInicial > volumenFinal) {
        return Math.round(((volumenInicial - volumenFinal) / tiempoDiferencia) * 3600);
    }
    return 0;
}

function estaRellenando(ultimoVolumen, nuevoVolumen) {
    return nuevoVolumen > ultimoVolumen + 150; // Considera rellenando si el volumen aumenta más de 150 unidades
