    let aumentos = 0;
    let estables = 0;

    for (let i = lecturas.length - 4; i < lecturas.length - 1; i++) {
        let diferencia = lecturas[i + 1].volumen - lecturas[i].volumen;
        if (diferencia < -0.5) {
            disminuciones++;
        } else if (diferencia > 0.5) {
            aumentos++;
        } else {
            estables++;
        }
    }

    if (disminuciones >= 3) return "alimentando";
    if (aumentos >= 3) return "rellenando";
    if (estables >= 3) return "estable";
    return "desconocido";
}

// Función para calcular el flujo (sin cambios)
function calcularFlujo(cola) {
    if (cola.length < 2) return 0;

    const primerRegistro = cola[0];
    const ultimoRegistro = cola[cola.length - 1];
    const tiempoDiferencia = (ultimoRegistro.tiempo - primerRegistro.tiempo) / 1000; // en segundos

    if (tiempoDiferencia > 0) {
        const volumenInicial = primerRegistro.volumen;
        const volumenFinal = ultimoRegistro.volumen;
        const diferenciaVolumen = volumenInicial - volumenFinal;

        // Convertir la diferencia de volumen a la escala de 0-40000
        const diferenciaEscalada = (diferenciaVolumen / (20000 - 4000)) * 40000;

        // Calcular el flujo en L/h, siempre positivo
        let flujoCalculado = Math.abs(Math.round((diferenciaEscalada / tiempoDiferencia) * 3600));
        return flujoCalculado;
    } else {
        return 0;
    }
}

