const ESTADO_TANQUE_KEY = "estadoTanque";
const INICIO_AUMENTO_KEY = "inicioAumento";
const ULTIMO_VOLUMEN_KEY = "ultimoVolumen";
const VALOR_FIJO_KEY = "valorFijo";

function detectarInicioAumento(cola, umbralAlcanzado) {
    for (let i = cola.length - 1; i > 0; i--) {
        if (cola[i].volumen <= cola[i-1].volumen) {
            return { tiempo: cola[i].tiempo, indice: i };
        }
        if (cola[i].tiempo <= umbralAlcanzado) {
            return { tiempo: cola[i].tiempo, indice: i };
        }
    }
    return { tiempo: cola[0].tiempo, indice: 0 };
}

function calcularPromedioAnterior(cola, indiceInicio) {
    const lecturasPrevias = cola.slice(0, indiceInicio);
    if (lecturasPrevias.length === 0) return 0;
    const suma = lecturasPrevias.reduce((acc, lectura) => acc + lectura.volumen, 0);
    return suma / lecturasPrevias.length;
}

function detectarCambioVolumen(ultimoVolumen, nuevoVolumen) {
    if (nuevoVolumen > ultimoVolumen + 70) return "rellenando";
    if (nuevoVolumen < ultimoVolumen - 5) return "vaciando";
    return "estable";
}

let colaLecturas = flow.get(COLA_LECTURAS_KEY) || [];
let estadoTanque = flow.get(ESTADO_TANQUE_KEY) || "vaciando";
let inicioAumento = flow.get(INICIO_AUMENTO_KEY) || null;
let ultimoVolumen = flow.get(ULTIMO_VOLUMEN_KEY) || msg.payload;
let valorFijo = flow.get(VALOR_FIJO_KEY) || 0;

const nuevaLectura = {
    tiempo: Date.now(),
    volumen: msg.payload
};

const nuevoEstado = detectarCambioVolumen(ultimoVolumen, nuevaLectura.volumen);

if (nuevoEstado === "rellenando" && estadoTanque !== "rellenando") {
    const { tiempo: tiempoInicio, indice: indiceInicio } = detectarInicioAumento(colaLecturas, nuevaLectura.tiempo);
    inicioAumento = tiempoInicio;
    flow.set(INICIO_AUMENTO_KEY, inicioAumento);

    valorFijo = calcularPromedioAnterior(colaLecturas, indiceInicio);
    flow.set(VALOR_FIJO_KEY, valorFijo);

    colaLecturas = colaLecturas.slice(0, indiceInicio);
} else if (nuevoEstado === "vaciando" && estadoTanque !== "vaciando") {
    // Iniciar nueva cola al comenzar a vaciar
    colaLecturas = [];
    inicioAumento = null;
    flow.set(INICIO_AUMENTO_KEY, null);
    valorFijo = 0;
    flow.set(VALOR_FIJO_KEY, valorFijo);
}

estadoTanque = nuevoEstado;
flow.set(ESTADO_TANQUE_KEY, estadoTanque);

if (estadoTanque !== "rellenando") {
    // Añadir la nueva lectura
    colaLecturas.push(nuevaLectura);

    // Mantener solo los últimos 5 minutos de datos
    const cincoMinutosAtras = Date.now() - 5 * 60 * 1000;
    colaLecturas = colaLecturas.filter(lectura => lectura.tiempo >= cincoMinutosAtras);
}

flow.set(COLA_LECTURAS_KEY, colaLecturas);
flow.set(ULTIMO_VOLUMEN_KEY, nuevaLectura.volumen);

return { payload: { colaLecturas, estadoTanque, valorFijo } };

function calcularFlujo(cola) {
    if (cola.length < 2) return 0;

    const primerRegistro = cola[0];
    const ultimoRegistro = cola[cola.length - 1];
    const tiempoDiferencia = (ultimoRegistro.tiempo - primerRegistro.tiempo) / 1000; // en segundos

    const volumenInicial = ((primerRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);
    const volumenFinal = ((ultimoRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);

    if (tiempoDiferencia > 0 && volumenInicial > volumenFinal) {
        return Math.round(((volumenInicial - volumenFinal) / tiempoDiferencia) * 3600);
    }
    return 0;
}

const { colaLecturas } = msg.payload;

let flujo = calcularFlujo(colaLecturas);

return { payload: flujo, estadoTanque: "vaciando" };

