const COLA_LECTURAS_KEY = "colaLecturas";
const ESTADO_TANQUE_KEY = "estadoTanque";
const ULTIMO_VOLUMEN_KEY = "ultimoVolumen";
const VALOR_FIJO_KEY = "valorFijo";

function detectarCambioVolumen(ultimoVolumen, nuevoVolumen) {
    if (nuevoVolumen > ultimoVolumen + 70) return "rellenando";
    if (nuevoVolumen < ultimoVolumen - 5) return "vaciando";
    return "estable";
}

function calcularFlujo(cola) {
    if (cola.length < 2) return 0;

    const primerRegistro = cola[0];
    const ultimoRegistro = cola[cola.length - 1];
    const tiempoDiferencia = (ultimoRegistro.tiempo - primerRegistro.tiempo) / 1000; // en segundos

    const volumenInicial = ((primerRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);
    const volumenFinal = ((ultimoRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);

    if (tiempoDiferencia > 0 && volumenInicial > volumenFinal) {
        return Math.round(((volumenInicial - volumenFinal) / tiempoDiferencia) * 3600);
    }
    return 0;
}

let colaLecturas = flow.get(COLA_LECTURAS_KEY) || [];
let estadoTanque = flow.get(ESTADO_TANQUE_KEY) || "vaciando";
let ultimoVolumen = flow.get(ULTIMO_VOLUMEN_KEY) || msg.payload;
let valorFijo = flow.get(VALOR_FIJO_KEY) || 0;

const nuevaLectura = {
    tiempo: Date.now(),
    volumen: msg.payload
};

const nuevoEstado = detectarCambioVolumen(ultimoVolumen, nuevaLectura.volumen);

if (nuevoEstado === "rellenando" && estadoTanque !== "rellenando") {
    valorFijo = calcularFlujo(colaLecturas);
    flow.set(VALOR_FIJO_KEY, valorFijo);
    colaLecturas = [];
} else if (nuevoEstado === "vaciando" && estadoTanque === "rellenando") {
    colaLecturas = [];
    valorFijo = 0;
    flow.set(VALOR_FIJO_KEY, valorFijo);
}

estadoTanque = nuevoEstado;
flow.set(ESTADO_TANQUE_KEY, estadoTanque);

if (estadoTanque !== "rellenando") {
    colaLecturas.push(nuevaLectura);
    if (colaLecturas.length > 60) { // Mantener máximo 5 minutos de datos (asumiendo una lectura por segundo)
        colaLecturas.shift();
    }
}

flow.set(COLA_LECTURAS_KEY, colaLecturas);
flow.set(ULTIMO_VOLUMEN_KEY, nuevaLectura.volumen);

let flujo = estadoTanque === "rellenando" ? valorFijo : calcularFlujo(colaLecturas);

return { payload: flujo, estadoTanque: estadoTanque };

// Obtener la cola de lecturas del mensaje
let cola = msg.payload;

// Obtener las últimas 10 lecturas de volumen
let lecturasVolumen = cola.map(registro => registro.volumen);
let numLecturas = lecturasVolumen.length;

// Inicializar contadores
let aumentos = 0;
let disminuciones = 0;
let estables = 0;

// Analizar las lecturas para detectar el estado
for (let i = Math.max(numLecturas - 10, 1); i < numLecturas; i++) {
    let diferencia = lecturasVolumen[i] - lecturasVolumen[i - 1];
    if (diferencia > 0) {
        aumentos++;
        disminuciones = 0;
        estables = 0;
    } else if (diferencia < 0) {
        disminuciones++;
        aumentos = 0;
        estables = 0;
    } else {
        estables++;
        aumentos = 0;
        disminuciones = 0;
    }
}

// Determinar el estado
let estado = flow.get("estado") || "Desconocido";

if (aumentos >= 4) {
    estado = "Relleno";
} else if (disminuciones >= 4) {
    estado = "Alimentación";
} else if (estables >= 10 && Math.abs(lecturasVolumen[numLecturas - 1] - lecturasVolumen[numLecturas - 2]) <= 20) {
    estado = "Estable";
}

// Guardar el estado en el contexto
flow.set("estado", estado);

// Añadir el estado al mensaje
msg.estado = estado;

// Pasar el mensaje al siguiente nodo
return msg;
